// Extract and validate incoming request
const inputData = $input.first().json;
const body = inputData.body || inputData;

const letterId = body.letterId;
const userId = body.userId;
const source = body.source || 'manual';

if (!letterId) {
  throw new Error('Missing required field: letterId');
}

console.log(`[PDF Workflow] Starting PDF generation for letter: ${letterId}`);

return {
  json: {
    letterId,
    userId: userId || null,
    source,
    timestamp: new Date().toISOString()
  }
};

// Build professional legal letter HTML matching TTML letterhead style
const letter = $('Fetch Letter from Supabase').first().json;
const profile = $('Fetch User Profile').first().json;

if (!letter || !letter.id) {
  throw new Error('Letter not found in database');
}

// Use letter_content for approved letters, fall back to ai_draft_content for other states
const content = letter.letter_content || letter.ai_draft_content || '';
const intake = letter.intake_data || {};
const title = letter.subject || letter.title || 'Legal Letter';
const isDraft = letter.status !== 'approved';
const senderName = profile?.full_name || intake.senderName || 'Sender';
const recipientName = intake.recipientName || 'Recipient';
const recipientAddress = intake.recipientAddress || '';
const recipientCompany = intake.recipientCompany || '';
const refNumber = `TTML-${letter.id.substring(0, 8).toUpperCase()}`;

// Format date - use approved_at for approved letters, created_at for drafts
const dateSource = letter.approved_at || letter.created_at;
const dateStr = new Date(dateSource).toLocaleDateString('en-US', {
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

// Escape HTML entities
function escapeHtml(text) {
  if (!text) return '';
  return String(text)
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')
    .replace(/'/g, '&#39;');
}

// Convert content newlines to HTML paragraphs
function contentToHtml(text) {
  if (!text) return '';
  return text
    .split('\n\n')
    .map(para => `<p style="margin: 0 0 12px 0; line-height: 1.6;">${escapeHtml(para).replace(/\n/g, '<br>')}</p>`)
    .join('');
}

const watermarkHtml = isDraft ? `
  <div style="position: fixed; top: 50%; left: 50%; transform: translate(-50%, -50%) rotate(-45deg); font-size: 80px; color: rgba(200, 200, 200, 0.3); font-weight: bold; z-index: -1; pointer-events: none;">
    DRAFT - NOT APPROVED
  </div>
` : '';

const html = `<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <style>
    @page {
      size: letter;
      margin: 25mm 20mm 25mm 25mm;
    }
    body {
      font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
      font-size: 11pt;
      color: #333333;
      line-height: 1.6;
      margin: 0;
      padding: 0;
    }
    .letterhead-bar {
      background-color: #1a1a2e;
      height: 8mm;
      width: 100%;
      margin-bottom: 12mm;
    }
    .letterhead-title {
      font-size: 18pt;
      font-weight: bold;
      color: #1a1a2e;
      margin: 0 0 2mm 0;
      letter-spacing: 0.5px;
    }
    .letterhead-subtitle {
      font-size: 9pt;
      color: #666666;
      margin: 0 0 8mm 0;
    }
    .letterhead-divider {
      border: none;
      border-top: 0.5px solid #e0e0e0;
      margin: 0 0 10mm 0;
    }
    .date {
      font-size: 10pt;
      margin-bottom: 12mm;
    }
    .recipient-block {
      margin-bottom: 8mm;
    }
    .recipient-name {
      font-weight: bold;
    }
    .subject-line {
      font-weight: bold;
      font-size: 11pt;
      margin: 8mm 0 10mm 0;
    }
    .letter-body {
      font-size: 10pt;
    }
    .closing-block {
      margin-top: 15mm;
    }
    .sender-name {
      font-weight: bold;
      margin-top: 15mm;
    }
    .footer {
      position: fixed;
      bottom: 0;
      left: 0;
      right: 0;
      border-top: 0.3px solid #e0e0e0;
      padding-top: 3mm;
      font-size: 8pt;
      color: #666666;
      display: flex;
      justify-content: space-between;
    }
    .footer-ref { float: left; }
    .footer-review { float: right; }
  </style>
</head>
<body>
  ${watermarkHtml}

  <!-- Letterhead -->
  <div class="letterhead-bar"></div>
  <div class="letterhead-title">TALK-TO-MY-LAWYER</div>
  <div class="letterhead-subtitle">Professional Legal Letter Services</div>
  <hr class="letterhead-divider">

  <!-- Date -->
  <div class="date">${escapeHtml(dateStr)}</div>

  <!-- Recipient -->
  <div class="recipient-block">
    <div class="recipient-name">${escapeHtml(recipientName)}</div>
    ${recipientCompany ? `<div>${escapeHtml(recipientCompany)}</div>` : ''}
    ${recipientAddress ? recipientAddress.split('\n').map(line => `<div>${escapeHtml(line)}</div>`).join('') : ''}
  </div>

  <!-- Subject -->
  ${title ? `<div class="subject-line">Re: ${escapeHtml(title)}</div>` : ''}

  <!-- Body -->
  <div class="letter-body">
    ${contentToHtml(content)}
  </div>

  <!-- Closing -->
  <div class="closing-block">
    <p>Sincerely,</p>
    <div class="sender-name">${escapeHtml(senderName)}</div>
  </div>

  <!-- Footer -->
  <div class="footer">
    <span class="footer-ref">Ref: ${escapeHtml(refNumber)}</span>
    ${!isDraft ? '<span class="footer-review">This letter has been reviewed by a licensed attorney.</span>' : ''}
  </div>
</body>
</html>`;

// Generate safe filename
const safeTitle = title.replace(/[^a-z0-9]/gi, '_').substring(0, 50) || 'letter';
const fileName = `${letter.id}/${safeTitle}.pdf`;

console.log(`[PDF Workflow] Built HTML for letter: ${letter.id}, content length: ${content.length}`);

return {
  json: {
    letterId: letter.id,
    userId: letter.user_id,
    title: title,
    fileName: fileName,
    storagePath: `letters/${fileName}`,
    html: html,
    isDraft: isDraft,
    refNumber: refNumber,
    timestamp: new Date().toISOString()
  }
};

// Process PDF result from HTML2PDF API
const htmlData = $('Build Letter HTML').first().json;
const pdfResult = $('Convert HTML to PDF').first();

// Check if PDF conversion succeeded
if (pdfResult && pdfResult.binary && pdfResult.binary.data) {
  console.log('[PDF Workflow] PDF generated successfully via HTML2PDF API');
  return {
    json: {
      letterId: htmlData.letterId,
      userId: htmlData.userId,
      fileName: htmlData.fileName,
      storagePath: htmlData.storagePath,
      pdfSource: 'html2pdf-api',
      timestamp: new Date().toISOString()
    },
    binary: {
      data: pdfResult.binary.data
    }
  };
}

// If no PDF binary, throw error
throw new Error('PDF conversion failed - no binary data returned from HTML2PDF API');
